import React, { useState } from "react";
import axios from "axios";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  const sendMessage = async () => {
    if (!input.trim()) return;
    setMessages([...messages, { from: "user", text: input }]);
    const res = await axios.post("http://localhost:8000/ask", {
      message: input,
      use_gemini: false, // Set to true to use Gemini
    });
    setMessages((msgs) => [...msgs, { from: "bot", text: res.data.answer }]);
    setInput("");
  };

  return (
    <div style={{ maxWidth: 600, margin: "auto", padding: 20 }}>
      <h2>Smart Logistics Assistant</h2>
      <div style={{ border: "1px solid #ccc", minHeight: 300, padding: 10 }}>
        {messages.map((msg, i) => (
          <div key={i} style={{ textAlign: msg.from === "user" ? "right" : "left" }}>
            <b>{msg.from === "user" ? "You" : "Assistant"}:</b> {msg.text}
          </div>
        ))}
      </div>
      <input
        value={input}
        onChange={(e) => setInput(e.target.value)}
        style={{ width: "80%", marginTop: 10 }}
        onKeyDown={(e) => e.key === "Enter" && sendMessage()}
      />
      <button onClick={sendMessage} style={{ width: "18%", marginLeft: 5 }}>
        Send
      </button>
    </div>
  );
}

export default App;
